    /**
     * 执行自定义SQL语句
     */
    public void execSQL(String sql) {
        SQLiteDatabase db = null;
        try {
            if (sql.contains("select")){
                Toast.makeText(context, R.string.strUnableSql, Toast.LENGTH_SHORT).show();
            }else if (sql.contains("insert") || sql.contains("update") || sql.contains("delete")){
                db = remotesDBHelper.getWritableDatabase();
                db.beginTransaction();
                db.execSQL(sql);
                db.setTransactionSuccessful();
                Toast.makeText(context, R.string.strSuccessSql, Toast.LENGTH_SHORT).show();
            }
        } catch (Exception e) {
            Toast.makeText(context, R.string.strErrorSql, Toast.LENGTH_SHORT).show();
            Log.e(TAG, "", e);
        } finally {
            if (db != null) {
                db.endTransaction();
                db.close();
            }
        }
    }
    /**
     * 数据查询，将function的信息提取出来
     */
    public List<Remote> getRemote(String[] strings){
        SQLiteDatabase db = null;
        Cursor cursor = null;
        try {
            db = remotesDBHelper.getReadableDatabase();
            cursor = db.query(RemoteDBHelper.TABLE_NAME,
                    ORDER_COLUMNS,
                    "Function = ?",
                    strings,
                    null, null, null);

            if (cursor.getCount() > 0) {
                List<Remote> remoteList = new ArrayList<Remote>(cursor.getCount());
                while (cursor.moveToNext()) {
                    Remote Remote = parseRemote(cursor);
                    remoteList.add(Remote);
                }
                return remoteList;
            }
        }
        catch (Exception e) {
            Log.e(TAG, "", e);
        }
        finally {
            if (cursor != null) {
                cursor.close();
            }
            if (db != null) {
                db.close();
            }
        }

        return null;
    }
    public void sqLite_insert(String device, String action) {
        SQLiteDatabase sqLiteDatabase = null;
        try {
            sqLiteDatabase = remotesDBHelper.getWritableDatabase();
            String time = TimeUtil.getTimeString();
            ContentValues values = new ContentValues();
            values.put(RemoteDBHelper.TIME, time);
            values.put(RemoteDBHelper.DEVICE, device);
            values.put(RemoteDBHelper.ACTION, action);
            sqLiteDatabase.insert(RemoteDBHelper.TABLE_NAME1, null, values);
        } catch (Exception e) {
            Log.e("tagh", "", e);
        } finally {
            if (sqLiteDatabase != null) {
                sqLiteDatabase.close();
            }

        }
    }


    public List<String> sqLite_read(String[] device) {
        List<String> list= null;
        SQLiteDatabase sqLiteDatabase = null;
        Cursor cursor=null;
        try {
            list = new ArrayList<>();
            String info = null;
            sqLiteDatabase = remotesDBHelper.getWritableDatabase();
             cursor = sqLiteDatabase.query(
                    RemoteDBHelper.TABLE_NAME1,
                    new String[]{RemoteDBHelper.TIME, RemoteDBHelper.DEVICE, RemoteDBHelper.ACTION},
                    "device = ?",
                    device,
                    null,
                    null,
                    RemoteDBHelper.TIME + " asc"
            );
            int TIME_index = cursor.getColumnIndex(RemoteDBHelper.TIME);
            int DEVICE_index = cursor.getColumnIndex(RemoteDBHelper.DEVICE);
            int ACTION_index = cursor.getColumnIndex(RemoteDBHelper.ACTION);
            while (cursor.moveToNext()) {
                String time = cursor.getString(TIME_index);
                String devices = cursor.getString(DEVICE_index);
                String action = cursor.getString(ACTION_index);
                info = time + "," + action;
                list.add(info);
            }

            sqLiteDatabase.close();
        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            if (cursor != null) {
                cursor.close();
            }
            if (sqLiteDatabase != null) {
                sqLiteDatabase.close();
            }
        }
        return list;                                           // 返回数据库的内容 info
    }


    private Remote parseRemote(Cursor cursor){
        Remote Remote = new Remote();
        Remote.id = (cursor.getInt(cursor.getColumnIndex("Id")));
        Remote.function = (cursor.getString(cursor.getColumnIndex("Function")));
        Remote.code = (cursor.getString(cursor.getColumnIndex("Code")));
        return Remote;
    }
